import React, { Component } from "react";
// import SearchForm from "./SearchForm";
import ResultList from "./ResultList";
import TopNav from "./Nav";
import TableTitle from "./TableTitle";
import API from "../utils/API";

class SearchResultContainer extends Component {
  state = {
    allUsers: [],
    filteredUsers: [],
    searchName: ""
  };

  // When this component mounts, search the Giphy API for pictures of kittens
  componentDidMount() {
    API.getUsers()
      .then(res => {
        console.log(res.data.results)
        this.setState(
          { allUsers: res.data.results, filteredUsers: res.data.results });
      })
  }

  handleInputChange = event => {
    // Getting the value and name of the input which triggered the change
    const { name, value } = event.target;

    // Updating the input's state
    this.setState({
      [name]: value
    }, ()=> {
      console.log(this.state.searchName)
      console.log(this.state.allUsers)
      const filtered = this.state.allUsers.filter(result => result.name.first.includes(this.state.searchName))
      console.log(filtered)
      this.setState({
        filteredUsers: filtered,
      })
    });
  };

  handleFormSubmit = event => {
    // Preventing the default behavior of the form submit (which is to refresh the page)
    event.preventDefault();

    // Alert the user their first and last name, clear `this.state.firstName` and `this.state.lastName`, clearing the inputs
    alert("hello");

  };

  render() {
    return (
      <div>
        <TopNav />
        <form>
          <input
            value={this.state.searchName}
            name="searchName"
            onChange={this.handleInputChange}
            type="text"
            placeholder="Search Employee"
          />

        </form>
        <TableTitle onClick= {this.handleSubmit} />
        <ResultList results={this.state.filteredUsers}/>
      </div>
    );
  }
}

export default SearchResultContainer;
